/*Table structure for table `message` */

DROP TABLE IF EXISTS `message`;

CREATE TABLE `message` (
  `message_id` int(8) unsigned NOT NULL AUTO_INCREMENT,
  `account_id` varchar(20) NOT NULL,
  `nickname` varchar(20) NOT NULL,
  `sex` enum('男','女','未填写','保密') NOT NULL DEFAULT '未填写',
  `QQ` varchar(20) NOT NULL DEFAULT '未填写',
  `xueli` varchar(20) DEFAULT '未填写',
  `province` varchar(20) DEFAULT '未填写',
  `city` varchar(20) DEFAULT '未填写',
  `jifen` int(8) NOT NULL DEFAULT '0',
  `Ubi` int(8) NOT NULL DEFAULT '0',
  `create_time` varchar(20) DEFAULT NULL,
  `update_time` varchar(20) DEFAULT NULL,
  `status` int(2) DEFAULT '1',
  PRIMARY KEY (`message_id`),
  UNIQUE KEY `account_id` (`account_id`),
  UNIQUE KEY `nickname` (`nickname`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

